// Food Analysis Python Script Execution
const { spawn } = require('child_process');
const fs = require('fs');
const path = require('path');

// Save image data to temporary file
const imageBuffer = $input.first().binary.data.data;
const tempImagePath = `/tmp/food_image_${Date.now()}.jpg`;
fs.writeFileSync(tempImagePath, imageBuffer);

// Execute Python food analysis script
return new Promise((resolve, reject) => {
  const pythonProcess = spawn('python3', [
    '/workspace/food_analysis.py',
    '--image', tempImagePath,
    '--user-data', JSON.stringify({
      height: $json.message.text?.includes('height:') ? parseFloat($json.message.text.split('height:')[1].split(' ')[0]) : 170,
      weight: $json.message.text?.includes('weight:') ? parseFloat($json.message.text.split('weight:')[1].split(' ')[0]) : 70,
      age: $json.message.text?.includes('age:') ? parseInt($json.message.text.split('age:')[1].split(' ')[0]) : 30,
      gender: $json.message.text?.includes('gender:') ? $json.message.text.split('gender:')[1].split(' ')[0] : 'male',
      ethnicity: $json.message.text?.includes('ethnicity:') ? $json.message.text.split('ethnicity:')[1].split(' ')[0] : 'caucasian',
      daily_calories: $json.message.text?.includes('calories:') ? parseInt($json.message.text.split('calories:')[1].split(' ')[0]) : 2000,
      sugar_limit: $json.message.text?.includes('sugar:') ? parseInt($json.message.text.split('sugar:')[1].split(' ')[0]) : 50
    })
  ]);

  let output = '';
  let errorOutput = '';

  pythonProcess.stdout.on('data', (data) => {
    output += data.toString();
  });

  pythonProcess.stderr.on('data', (data) => {
    errorOutput += data.toString();
  });

  pythonProcess.on('close', (code) => {
    // Clean up temporary file
    try {
      fs.unlinkSync(tempImagePath);
    } catch (err) {
      console.log('Could not delete temp file:', err);
    }

    if (code === 0) {
      try {
        const result = JSON.parse(output);
        resolve([{ json: result }]);
      } catch (parseError) {
        reject(new Error(`Failed to parse Python output: ${parseError.message}\nOutput: ${output}`));
      }
    } else {
      reject(new Error(`Python script failed with code ${code}: ${errorOutput}`));
    }
  });
});

// Process analysis results and format response
const analysis = $input.first().json;

// Calculate compliance score
let complianceScore = 100;
let warnings = [];
let recommendations = [];

// Check calorie compliance
const calorieRatio = analysis.total_calories / analysis.user_data.daily_calories;
if (calorieRatio > 0.3) {
  complianceScore -= 20;
  warnings.push(`High calorie content: ${analysis.total_calories} calories (${Math.round(calorieRatio * 100)}% of daily limit)`);
}

// Check sugar compliance
const sugarRatio = analysis.total_sugar / analysis.user_data.sugar_limit;
if (sugarRatio > 0.2) {
  complianceScore -= 15;
  warnings.push(`High sugar content: ${analysis.total_sugar}g (${Math.round(sugarRatio * 100)}% of daily limit)`);
}

// Check sodium compliance
if (analysis.total_sodium > 600) {
  complianceScore -= 10;
  warnings.push(`High sodium content: ${analysis.total_sodium}mg`);
}

// Generate recommendations
if (analysis.total_calories > analysis.user_data.daily_calories * 0.25) {
  recommendations.push('Consider reducing portion size or choosing lower-calorie alternatives');
}

if (analysis.total_sugar > analysis.user_data.sugar_limit * 0.15) {
  recommendations.push('Look for sugar-free or reduced-sugar versions of ingredients');
}

if (analysis.total_fat > 30) {
  recommendations.push('Consider cooking methods that reduce fat content (baking, grilling)');
}

// Determine compliance status
let complianceStatus = 'COMPLIANT';
if (complianceScore < 70) {
  complianceStatus = 'NON_COMPLIANT';
} else if (complianceScore < 85) {
  complianceStatus = 'MODERATE_COMPLIANCE';
}

// Format response message
let responseMessage = `ðŸ½ï¸ *Food Analysis Results*\n\n`;
responseMessage += `*Cuisine:* ${analysis.cuisine_type}\n`;
responseMessage += `*Dish:* ${analysis.dish_name}\n\n`;
responseMessage += `*Nutritional Information:*\n`;
responseMessage += `â€¢ Calories: ${analysis.total_calories}\n`;
responseMessage += `â€¢ Protein: ${analysis.total_protein}g\n`;
responseMessage += `â€¢ Carbs: ${analysis.total_carbs}g\n`;
responseMessage += `â€¢ Fat: ${analysis.total_fat}g\n`;
responseMessage += `â€¢ Sugar: ${analysis.total_sugar}g\n`;
responseMessage += `â€¢ Sodium: ${analysis.total_sodium}mg\n\n`;

responseMessage += `*Compliance Status:* ${complianceStatus}\n`;
responseMessage += `*Score:* ${complianceScore}/100\n\n`;

if (warnings.length > 0) {
  responseMessage += `*âš ï¸ Warnings:*\n`;
  warnings.forEach(warning => {
    responseMessage += `â€¢ ${warning}\n`;
  });
  responseMessage += `\n`;
}

if (recommendations.length > 0) {
  responseMessage += `*ðŸ’¡ Recommendations:*\n`;
  recommendations.forEach(rec => {
    responseMessage += `â€¢ ${rec}\n`;
  });
}

// Add ingredient breakdown
if (analysis.ingredients && analysis.ingredients.length > 0) {
  responseMessage += `\n*Ingredient Breakdown:*\n`;
  analysis.ingredients.slice(0, 5).forEach(ingredient => {
    responseMessage += `â€¢ ${ingredient.name}: ${ingredient.calories} cal, ${ingredient.protein}g protein\n`;
  });
  if (analysis.ingredients.length > 5) {
    responseMessage += `â€¢ ... and ${analysis.ingredients.length - 5} more ingredients\n`;
  }
}

return [{
  json: {
    chat_id: $('Webhook Trigger').first().json.message.chat.id,
    text: responseMessage,
    parse_mode: 'Markdown',
    analysis_data: analysis,
    compliance_status: complianceStatus,
    compliance_score: complianceScore
  }
}];
