// Extract dietary information from message text
const messageText = $input.first().json.message.text || '';
const chatId = $input.first().json.message.chat.id;
const userId = $input.first().json.message.from.id;

// Parse dietary measurements from text
const dietaryData = {
  chatId: chatId,
  userId: userId,
  weight: null,
  height: null,
  age: null,
  allergies: [],
  goals: '',
  activityLevel: 'moderate',
  gender: 'unknown'
};

// Extract weight (in kg)
const weightMatch = messageText.match(/weight[\s:]*([0-9.]+)\s*kg/i);
if (weightMatch) {
  dietaryData.weight = parseFloat(weightMatch[1]);
}

// Extract height (in cm)
const heightMatch = messageText.match(/height[\s:]*([0-9.]+)\s*cm/i);
if (heightMatch) {
  dietaryData.height = parseFloat(heightMatch[1]);
}

// Extract age
const ageMatch = messageText.match(/age[\s:]*([0-9]+)/i);
if (ageMatch) {
  dietaryData.age = parseInt(ageMatch[1]);
}

// Extract allergies
const allergyMatch = messageText.match(/allerg[ies]*[\s:]*([^,]+)/i);
if (allergyMatch) {
  dietaryData.allergies = allergyMatch[1].split(',').map(a => a.trim().toLowerCase());
}

// Extract goals
const goalMatch = messageText.match(/goal[s]*[\s:]*([^,]+)/i);
if (goalMatch) {
  dietaryData.goals = goalMatch[1].trim();
}

// Extract gender
const genderMatch = messageText.match(/gender[\s:]*([^,]+)/i);
if (genderMatch) {
  dietaryData.gender = genderMatch[1].trim().toLowerCase();
}

// Extract activity level
const activityMatch = messageText.match(/activity[\s:]*([^,]+)/i);
if (activityMatch) {
  dietaryData.activityLevel = activityMatch[1].trim().toLowerCase();
}

return {
  json: {
    dietaryData: dietaryData,
    hasPhoto: true,
    messageText: messageText
  }
};

// Format the final response for Telegram
const foodData = $input.first().json.data || {};
const mealPlanData = $input.first().json.data || {};
const dietaryData = $input.first().json.dietaryData || {};

// Extract data from API responses
const identifiedFoods = foodData.identifiedFoods || [];
const totalCalories = foodData.totalCalories || 0;
const totalProtein = foodData.totalProtein || 0;
const totalCarbs = foodData.totalCarbs || 0;
const totalFat = foodData.totalFat || 0;
const totalFiber = foodData.totalFiber || 0;

const bmr = mealPlanData.bmr || 0;
const tdee = mealPlanData.tdee || 0;
const caloricGoal = mealPlanData.caloric_goal || 0;
const nutritionalGoals = mealPlanData.nutritional_goals || {};
const mealPlan = mealPlanData.meal_plan || {};
const safetyWarnings = mealPlanData.safety_warnings || [];

// Format 7-day meal plan
function formatMealPlan(mealPlan) {
  if (!mealPlan || Object.keys(mealPlan).length === 0) {
    return '\nðŸ½ï¸ **7-DAY MEAL PLAN**\n\n*Meal plan generation in progress...*\n';
  }

  let formatted = '\nðŸ½ï¸ **7-DAY MEAL PLAN**\n\n';

  Object.entries(mealPlan).forEach(([day, meals]) => {
    formatted += `**${day}**\n`;

    Object.entries(meals).forEach(([mealType, meal]) => {
      const emoji = {
        breakfast: 'ðŸŒ…',
        lunch: 'â˜€ï¸',
        dinner: 'ðŸŒ™',
        snacks: 'ðŸŽ'
      }[mealType] || 'ðŸ½ï¸';

      formatted += `${emoji} **${mealType.charAt(0).toUpperCase() + mealType.slice(1)}** (${meal.calories} cal)\n`;
      if (meal.foods && meal.foods.length > 0) {
        formatted += `   â€¢ ${meal.foods.join(', ')}\n`;
      }
      formatted += `   â€¢ Protein: ${meal.protein}g | Carbs: ${meal.carbs}g | Fat: ${meal.fat}g\n\n`;
    });
  });

  return formatted;
}

// Format safety warnings
function formatSafetyWarnings(warnings) {
  if (!warnings || warnings.length === 0) return '';

  let formatted = '\nâš ï¸ **SAFETY WARNINGS**\n\n';

  warnings.forEach(warning => {
    formatted += `ðŸš¨ **${warning.allergen.toUpperCase()} ALLERGY DETECTED**\n`;
    if (warning.found_in && warning.found_in.length > 0) {
      formatted += `Found in: ${warning.found_in.join(', ')}\n\n`;
    }
  });

  return formatted;
}

// Format current meal analysis
function formatCurrentMealAnalysis() {
  if (identifiedFoods.length === 0) {
    return '\nðŸ“Š **CURRENT MEAL ANALYSIS**\n\n*No food items identified in the image.*\n';
  }

  let formatted = '\nðŸ“Š **CURRENT MEAL ANALYSIS**\n\n';

  formatted += `**Identified Foods:**\n`;
  identifiedFoods.forEach(food => {
    formatted += `â€¢ ${food.name} (${food.confidence ? Math.round(food.confidence * 100) : 0}% confidence)\n`;
  });

  formatted += `\n**Nutritional Breakdown:**\n`;
  formatted += `â€¢ Calories: ${totalCalories}\n`;
  formatted += `â€¢ Protein: ${totalProtein}g\n`;
  formatted += `â€¢ Carbs: ${totalCarbs}g\n`;
  formatted += `â€¢ Fat: ${totalFat}g\n`;
  formatted += `â€¢ Fiber: ${totalFiber}g\n\n`;

  if (caloricGoal > 0) {
    const remainingCalories = caloricGoal - totalCalories;
    formatted += `**Daily Progress:**\n`;
    formatted += `â€¢ Daily Goal: ${caloricGoal} calories\n`;
    formatted += `â€¢ Remaining: ${remainingCalories} calories\n`;
    formatted += `â€¢ Progress: ${Math.round((totalCalories / caloricGoal) * 100)}%\n\n`;
  }

  return formatted;
}

// Create the complete response
const response = `
ðŸ¥— **PERSONALIZED NUTRITIONAL PLAN** ðŸ¥—

ðŸ“‹ **YOUR METABOLIC PROFILE**
â€¢ BMR (Basal Metabolic Rate): ${bmr} calories/day
â€¢ TDEE (Total Daily Energy Expenditure): ${tdee} calories/day
â€¢ Recommended Daily Intake: ${caloricGoal} calories/day

ðŸŽ¯ **MACRONUTRIENT TARGETS**
â€¢ Protein: ${nutritionalGoals.protein || 0}g (${Math.round((nutritionalGoals.protein || 0) * 4)} cal)
â€¢ Carbohydrates: ${nutritionalGoals.carbs || 0}g (${Math.round((nutritionalGoals.carbs || 0) * 4)} cal)
â€¢ Fat: ${nutritionalGoals.fat || 0}g (${Math.round((nutritionalGoals.fat || 0) * 9)} cal)

${formatCurrentMealAnalysis()}
${formatMealPlan(mealPlan)}
${formatSafetyWarnings(safetyWarnings)}

ðŸ’¡ **TIPS FOR SUCCESS**
â€¢ Stay hydrated with 8-10 glasses of water daily
â€¢ Track your meals consistently
â€¢ Listen to your body's hunger cues
â€¢ Include variety in your diet
â€¢ Don't forget to exercise regularly!

ðŸ¤– *This plan was generated using AI-powered food recognition and personalized dietary algorithms.*
`;

return {
  json: {
    chatId: dietaryData.chatId || 'unknown',
    message: response,
    parseMode: 'Markdown'
  }
};

