// Extract dietary information from message text
const messageText = $input.first().json.message.text || '';
const chatId = $input.first().json.message.chat.id;
const userId = $input.first().json.message.from.id;

// Parse dietary measurements from text
// Expected format: "Weight: 70kg, Height: 175cm, Age: 30, Allergies: nuts, Goals: lose 5kg"
const dietaryData = {
  chatId: chatId,
  userId: userId,
  weight: null,
  height: null,
  age: null,
  allergies: [],
  goals: '',
  activityLevel: 'moderate',
  gender: 'unknown'
};

// Extract weight (in kg)
const weightMatch = messageText.match(/weight[\s:]*([0-9.]+)\s*kg/i);
if (weightMatch) {
  dietaryData.weight = parseFloat(weightMatch[1]);
}

// Extract height (in cm)
const heightMatch = messageText.match(/height[\s:]*([0-9.]+)\s*cm/i);
if (heightMatch) {
  dietaryData.height = parseFloat(heightMatch[1]);
}

// Extract age
const ageMatch = messageText.match(/age[\s:]*([0-9]+)/i);
if (ageMatch) {
  dietaryData.age = parseInt(ageMatch[1]);
}

// Extract allergies
const allergyMatch = messageText.match(/allerg[ies]*[\s:]*([^,]+)/i);
if (allergyMatch) {
  dietaryData.allergies = allergyMatch[1].split(',').map(a => a.trim().toLowerCase());
}

// Extract goals
const goalMatch = messageText.match(/goal[s]*[\s:]*([^,]+)/i);
if (goalMatch) {
  dietaryData.goals = goalMatch[1].trim();
}

// Extract gender
const genderMatch = messageText.match(/gender[\s:]*([^,]+)/i);
if (genderMatch) {
  dietaryData.gender = genderMatch[1].trim().toLowerCase();
}

// Extract activity level
const activityMatch = messageText.match(/activity[\s:]*([^,]+)/i);
if (activityMatch) {
  dietaryData.activityLevel = activityMatch[1].trim().toLowerCase();
}

return {
  json: {
    dietaryData: dietaryData,
    hasPhoto: true,
    messageText: messageText
  }
};

// Food Recognition and Calorie Estimation using Computer Vision
// This would typically call an external API like Hugging Face, AWS SageMaker, or Google Vision

const imageData = $input.first().json;
