// Dietary Planning Algorithm with BMR/TDEE Calculations
const { mealAnalysis, dietaryData } = $input.first().json;

// BMR Calculation using Mifflin-St Jeor Equation
function calculateBMR(weight, height, age, gender) {
  if (gender === 'male') {
    return 10 * weight + 6.25 * height - 5 * age + 5;
  } else if (gender === 'female') {
    return 10 * weight + 6.25 * height - 5 * age - 161;
  } else {
    // Average of male and female formulas for unknown gender
    return (10 * weight + 6.25 * height - 5 * age + 5 + 10 * weight + 6.25 * height - 5 * age - 161) / 2;
  }
}

// TDEE Calculation based on activity level
function calculateTDEE(bmr, activityLevel) {
  const activityMultipliers = {
    'sedentary': 1.2,
    'light': 1.375,
    'moderate': 1.55,
    'active': 1.725,
    'very_active': 1.9
  };

  return bmr * (activityMultipliers[activityLevel] || 1.55);
}

// Calculate BMR and TDEE
const bmr = calculateBMR(dietaryData.weight, dietaryData.height, dietaryData.age, dietaryData.gender);
const tdee = calculateTDEE(bmr, dietaryData.activityLevel);

// Determine caloric goal based on user goals
let caloricGoal = tdee;
if (dietaryData.goals.toLowerCase().includes('lose')) {
  caloricGoal = tdee - 500; // 500 calorie deficit for weight loss
} else if (dietaryData.goals.toLowerCase().includes('gain')) {
  caloricGoal = tdee + 500; // 500 calorie surplus for weight gain
}

// Macronutrient distribution (40% carbs, 30% protein, 30% fat)
const proteinCalories = caloricGoal * 0.30;
const carbCalories = caloricGoal * 0.40;
const fatCalories = caloricGoal * 0.30;

const proteinGrams = proteinCalories / 4; // 4 calories per gram
const carbGrams = carbCalories / 4; // 4 calories per gram
const fatGrams = fatCalories / 9; // 9 calories per gram

// Generate 7-day meal plan
const mealPlan = generateMealPlan(caloricGoal, proteinGrams, carbGrams, fatGrams, dietaryData);

// Safety warnings for allergies
const safetyWarnings = generateSafetyWarnings(dietaryData.allergies, mealPlan);

// Progress tracking metrics
const progressMetrics = {
  currentMealCalories: mealAnalysis.totalCalories,
  dailyCalorieGoal: caloricGoal,
  remainingCalories: caloricGoal - mealAnalysis.totalCalories,
  proteinProgress: (mealAnalysis.totalProtein / proteinGrams) * 100,
  carbProgress: (mealAnalysis.totalCarbs / carbGrams) * 100,
  fatProgress: (mealAnalysis.totalFat / fatGrams) * 100
};

function generateMealPlan(calories, protein, carbs, fat, userData) {
  const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
  const mealPlan = {};

  days.forEach(day => {
    mealPlan[day] = {
      breakfast: {
        calories: Math.round(calories * 0.25),
        protein: Math.round(protein * 0.25 * 10) / 10,
        carbs: Math.round(carbs * 0.25 * 10) / 10,
        fat: Math.round(fat * 0.25 * 10) / 10,
        foods: ["Oatmeal with berries", "Greek yogurt", "Almonds"]
      },
      lunch: {
        calories: Math.round(calories * 0.35),
        protein: Math.round(protein * 0.35 * 10) / 10,
        carbs: Math.round(carbs * 0.35 * 10) / 10,
        fat: Math.round(fat * 0.35 * 10) / 10,
        foods: ["Grilled chicken salad", "Quinoa", "Mixed vegetables"]
      },
      dinner: {
        calories: Math.round(calories * 0.30),
        protein: Math.round(protein * 0.30 * 10) / 10,
        carbs: Math.round(carbs * 0.30 * 10) / 10,
        fat: Math.round(fat * 0.30 * 10) / 10,
        foods: ["Salmon fillet", "Sweet potato", "Steamed broccoli"]
      },
      snacks: {
        calories: Math.round(calories * 0.10),
        protein: Math.round(protein * 0.10 * 10) / 10,
        carbs: Math.round(carbs * 0.10 * 10) / 10,
        fat: Math.round(fat * 0.10 * 10) / 10,
        foods: ["Apple with almond butter", "Protein shake"]
      }
    };
  });

  return mealPlan;
}

function generateSafetyWarnings(allergies, mealPlan) {
  const warnings = [];

  if (allergies.length === 0) {
    return warnings;
  }

  // Check for common allergens in meal plan
  const allergenKeywords = {
    'nuts': ['almond', 'walnut', 'peanut', 'cashew', 'pistachio'],
    'dairy': ['milk', 'cheese', 'yogurt', 'butter', 'cream'],
    'gluten': ['wheat', 'bread', 'pasta', 'flour', 'oats'],
    'eggs': ['egg', 'mayonnaise', 'custard'],
    'soy': ['soy', 'tofu', 'tempeh', 'soy sauce'],
    'fish': ['salmon', 'tuna', 'cod', 'fish'],
    'shellfish': ['shrimp', 'crab', 'lobster', 'shellfish']
  };

  allergies.forEach(allergy => {
    const keywords = allergenKeywords[allergy] || [allergy];
    const foundAllergens = [];

    Object.values(mealPlan).forEach(day => {
      Object.values(day).forEach(meal => {
        meal.foods.forEach(food => {
          keywords.forEach(keyword => {
            if (food.toLowerCase().includes(keyword)) {
              foundAllergens.push(food);
            }
          });
        });
      });
    });

    if (foundAllergens.length > 0) {
      warnings.push({
        type: 'allergy_warning',
        allergen: allergy,
        foundIn: [...new Set(foundAllergens)],
        severity: 'high'
      });
    }
  });

  return warnings;
}

return {
  json: {
    bmr: Math.round(bmr),
    tdee: Math.round(tdee),
    caloricGoal: Math.round(caloricGoal),
    macronutrientGoals: {
      protein: Math.round(proteinGrams * 10) / 10,
      carbs: Math.round(carbGrams * 10) / 10,
      fat: Math.round(fatGrams * 10) / 10
    },
    mealPlan: mealPlan,
    safetyWarnings: safetyWarnings,
    progressMetrics: progressMetrics,
    mealAnalysis: mealAnalysis
  }
};

// Format the final response for Telegram
